svg.script:

// var svgNs = document.documentElement.getAttributeNS('http://www.w3.org/2000/svg', 'xmlns');
var svgNs = 'http://www.w3.org/2000/svg';

function init(){
  // First we'll try copying the body from 0.0 to -1.0 - works fine.
  document.getElementById("tile:-1.0").appendChild(document.getElementById("tile:0.0").childNodes[1].cloneNode(false));

  // Now we'll try creating a new body for 0.-1 from a string with the DOMParser - creates an 'invisible' element.
  document.getElementById("tile:0.-1").appendChild(document.importNode(new DOMParser().parseFromString('<rect xmlns="'+svgNs+'" x="0" y="0" width="100" height="100" class="background"/>', "text/xml").documentElement, true));
}
